Consulta.sql:
with cons as( select id_resolucion, resolucion, identificador, res_modificatoria, (case when res_modificatoria IS NOT NULL THEN (SELECT id_res_modificatoria FROM resolucion_modificacion where id_res_original=id_resolucion)
end) as modif , (case when res_modificatoria IS NULL THEN (SELECT id_res_original FROM resolucion_modificacion where (id_res_original=id_resolucion OR id_res_modificatoria=id_resolucion )) end) as modificatoria
from resolucion where identificador = '@IDENTIFICADOR' and resolucion = '@id' ), bep as ( UPDATE resolucion SET res_modificatoria=null where id_resolucion=(select modificatoria from cons)
), del as ( delete from resolucion where id_resolucion= (select modif from cons) ), del2 as ( delete from resolucion where id_resolucion= (select id_resolucion from cons ) ) select * from cons

